
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

const Slider = () => {
  const slides = [
    {
      image: "data:image/jpeg;base64,", 
      title: "Grow Your Freelance Career",
      subtitle: "Join our marketplace and start earning from your skills today.",
      description: "A freelancer is a self-employed individual who offers services to clients on a contract basis, often in fields like writing, design, programming, or marketing.",
      buttonText: "Hire Me",
    },
    {
      image:  "data:image/jpeg;base64,",
      title: "Find Expert Freelancers",
      subtitle: "Connect with top-rated professionals for your business needs.",
      description: "A freelancer works independently, taking on jobs from different clients without being tied to one company.",
      buttonText: "Start Your Journey",
    },
    {
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTmM5eofN7OKN1FoED89cPvoyS3XNgbYC3_WQ&s",
      title: "Post Your Task",
      subtitle: "Get instant responses from skilled freelancers ready to work.",
      description: "A freelancer is someone who turns skills into freedom—choosing when, where, and how to work on their own terms.",
      buttonText: "Get a Quote",
    },
  ];

  return (
    <div className=" w-9/12 mx-auto rounded">
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={10}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        autoplay={{ delay: 4000 }}
        loop={true}
        className="rounded"
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={index}>
            <div className="relative w-full h-[500px]">
              <img
                src={slide.image}
                alt={`Slide ${index}`}
                className="w-full h-full object-cover"
              />
              <div className="absolute inset-0 bg-black/40 flex flex-col items-center justify-center text-white text-center p-4 space-y-6">
                <h1 className="text-4xl md:text-5xl font-bold drop-shadow-lg">
                  {slide.title}
                </h1>
                <p className="text-lg md:text-xl">{slide.subtitle}</p>
                <p className="text-base md:text-lg px-8">{slide.description}</p>
                <button className="bg-green-600 hover:bg-green-700 px-6 py-3 rounded text-lg font-semibold transition">
                  {slide.buttonText}
                </button>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default Slider;









